/* quiz.component.scss */

.quiz-container {
    max-width: 500px;
    margin: 0 auto;
    padding: 20px;
    border: 1px solid #ccc;
    border-radius: 5px;
    background-color: #f7f7f7;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
  
    h2 {
      font-size: 24px;
      margin-bottom: 20px;
      text-align: center;
    }
  
    .question-text {
      font-size: 18px;
      margin-bottom: 15px;
    }
  
    .answer-choices {
      list-style-type: none;
      padding: 0;
  
      li {
        margin-bottom: 10px;
      }
  
      input[type="radio"] {
        margin-right: 10px;
      }
    }
  
    button {
      display: block;
      margin: 20px auto;
      padding: 10px 20px;
      background-color: #007BFF;
      color: #fff;
      border: none;
      border-radius: 5px;
      cursor: pointer;
      font-size: 16px;
  
      &:hover {
        background-color: #0056b3;
      }
    }
  
    .score {
      text-align: center;
      font-size: 20px;
      margin-top: 20px;
    }
  }
