/* Add styles to the form container */
.form-container {
    width: 400px;
    margin: 0 auto;
    padding: 20px;
    background-color: #f4f4f4;
    border: 1px solid #ccc;
    border-radius: 5px;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.1);
  }
  
  /* Style form labels */
  .form-group label {
    font-weight: bold;
  }
  
  /* Style form input fields */
  .form-group input[type="text"],
  .form-group textarea {
    width: 100%;
    padding: 8px;
    margin-bottom: 10px;
    border: 1px solid #ccc;
    border-radius: 3px;
    font-size: 16px;
  }
  
  /* Style the submit button */
  button[type="submit"] {
    background-color: #007bff;
    color: #fff;
    padding: 10px 20px;
    border: none;
    border-radius: 3px;
    cursor: pointer;
  }
  
  /* Add margin to the list of reclamation requests */
  .reclamation-list {
    margin-top: 20px;
  }
  
  /* Style individual reclamation items in the list */
  .reclamation-item {
    border: 1px solid #ddd;
    padding: 10px;
    margin: 10px 0;
    border-radius: 3px;
    background-color: #fff;
  }
